import React, { useState, useEffect } from 'react'
import UserItems from '../useritems/UserItems'
import { useNavigate } from "react-router-dom";
import Form from '../form/Form';
import UserProfileModal from '../userprofilemodal/UserProfileModal';
import './UserItemPage.css'

function UserItemPage({userItems, deleteItem, items1, setItems, addItem, setUserItems, setShowEditForm, showEditForm, handleUserInfoEditClick, updateUserInfo}) {

  let navigate = useNavigate()
  const [userInfoOnly, setUserInfoOnly] = useState({})

  const {bio, full_name, location, items, image} = userItems

  const mappedUserItems = items.map(item => {
    return <UserItems 
      key={item.id}
      item={item}
      deleteItem={deleteItem}
    />
  })

  useEffect(() => {
    fetch('http://localhost:9292/users/infoonly')
      .then(res => res.json())
      .then(data => setUserInfoOnly(data))
  }, [])

  function handleCloseModal () {
    setShowEditForm((showEditForm) => !showEditForm)
  }

    return (
      <>
        <div className="homeButtonContainer">
          <button className="backToHomeButton" onClick={() => navigate("/")}> BACK TO HOME </button>
        </div>

        {showEditForm? null: <UserProfileModal  handleCloseModal={handleCloseModal} showEditForm={showEditForm} updateUserInfo={updateUserInfo} />}

        <div className="userInfo">
          <img className="userImage" src={userInfoOnly.image} alt={userInfoOnly.full_name}></img>
          <h1>{userInfoOnly.full_name}</h1>
          <h3>Location: {userInfoOnly.location}</h3>
          <h3>Bio: {userInfoOnly.bio}</h3>
          <button className="editUserButton"onClick={handleUserInfoEditClick}>Edit Profile</button>
        </div>

        <div className="useritemspagecontainer">
          {mappedUserItems}
        </div>

        <Form items={items1} setItems={setItems} addItem={addItem} />
      </>
    )
}

export default UserItemPage
