import React, {useEffect, useState} from 'react';
import { BrowserRouter as Router, Routes, Route } from "react-router-dom"
import Home from './home/Home'
import RequestPage from './requestpage/RequestPage';
import UserItemPage from './useritempage/UserItemPage';
import './App.css';

function App() {
  const [userItems, setUserItems] = useState({}) //grabbing the users, then grabbing the items
  const [showClickedItem, setShowClickedItem] = useState({})
  const [items, setItems] = useState([]) // displaying all items
  const [cart, setCart] = useState('')
  const [spin, setSpin] = useState('')
  const [money, setMoney] = useState(0)
  const [showEditForm, setShowEditForm] = useState(true)

  useEffect(() => {
    fetch('http://localhost:9292/items')
        .then(res => res.json())
        .then(items => setItems(items))
}, [])


  useEffect(() => {
    fetch('http://localhost:9292/users')
      .then(res => res.json())
      .then(data => setUserItems(data))
      console.log(data)
}, [userItems])


function handleUserInfoEditClick () {
  setShowEditForm((showEditForm) => !showEditForm)
}

function addItem(newestItem) {
  setUserItems({...userItems, items: [...userItems.items, newestItem]}) 
  setItems([...items, newestItem])
}

function updateUserInfo(updatedUserInfo) {
  const newUserInfo = userItems.map(userInfo => {
    if (userInfo.id === updatedUserInfo.id) {
      return updatedUserInfo
    } else {
      return userItems
    }
  })
  setUserItems(updatedUserInfo)
}


function handleRequest (item1, item2) {
alert(`You successfully swap't your item, good for you. You owe $${money}`)

  fetch(`http://localhost:9292/item/swap/${item1.id}/${item2.id}`, {
    method: "PATCH",
    headers: {
      "Content-Type": "application/json",
    }
  })
 .then(resp => resp.json())
 .then(() => {  
  setItems(items.map(item => {

    if(item.id === item1.id) {
      return {...item, user_id: item2.user_id}
    }
    else if(item.id === item2.id){
      return {...item, user_id: item1.user_id}
    }
    else {
      return item
    }
  }))

  setUserItems({...userItems, items: userItems.items.map( item => {
    if(item.id === item1.id) {
      return item2
    }
    else {
      return item
    }
  })
})
})
}

  function deleteItem(deletedItem) {
    fetch(`http://localhost:9292/itemdelete/${deletedItem.id}`, {
      method: "DELETE",
    })
    setUserItems({...userItems, items: userItems.items.filter(item => item.id !== deletedItem.id)})
  }

  return (
    <>
    <div className="logocontainer">
    <h1> <img className="logoimage" src="https://cdn-dmppo.nitrocdn.com/MQtLRDiaOJqaiAMiKiWzfYWFElqSrNJq/assets/static/optimized/rev-084e1d7/wp-content/uploads/2020/11/HCT_FoodSwapIcon.png"></img>SWAP'T</h1>
    </div>


    <Router>
    <Routes>
      <Route exact path='/' element={<Home showClickedItem={showClickedItem} setShowClickedItem={setShowClickedItem} items={items}/>}/>
      <Route exact path="/request" element={<RequestPage spin={spin} setSpin={setSpin} setMoney={setMoney} handleRequest={handleRequest} cart={cart} setCart={setCart} userItems={userItems} showClickedItem={showClickedItem} setShowClickedItem={setShowClickedItem}/>}/>
      <Route exact path ="/useritempage" element={<UserItemPage userItems={userItems} setUserItems={setUserItems} deleteItem={deleteItem} items={items} setItems={setItems} addItem={addItem} setShowEditForm={setShowEditForm} showEditForm={showEditForm} handleUserInfoEditClick={handleUserInfoEditClick} updateUserInfo={updateUserInfo}/>}/>
    </Routes>
    </Router>

    </>
  )
}

export default App;
