import React, { useState } from 'react';
import { useNavigate } from "react-router-dom";
import ItemsContainer from '../itemscontainer/ItemsContainer';
import './Home.css';
//import ConfirmModal from './ConfirmModal';

function Home({setShowClickedItem, showClickedItem, items, setToggleTradeItem, toggleTradeItem, setToggleNewInfo, toggleNewInfo}) {
    let navigate = useNavigate()

    const [searchItem, setSearchItem] = useState("")

    function handleSearchInput (e) {
        setSearchItem(e.target.value)
    }

    const filteredListItem = items.filter(item => {
        return item.item_name.toLowerCase().includes(searchItem.toLowerCase())
    })

    return (
        <>
            <div className="buttonContainer">
            <button className="myItemsButton" onClick={() => navigate("/useritempage") }>MY ITEMS</button>
            </div>

            <div className="inputContainer">
            <input className="inputBox"type="text" placeholder="Search for items" onChange={handleSearchInput}></input>
            </div>

            <ItemsContainer items={filteredListItem} showClickedItem={showClickedItem} setShowClickedItem={setShowClickedItem} setToggleTradeItem={setToggleTradeItem} toggleTradeItem={toggleTradeItem} setToggleNewInfo={setToggleNewInfo} toggleNewInfo={setToggleNewInfo}/>

        </>
    )
}

export default Home;
